create table conversations (botHasBeenUnmasked boolean, end_of_conversation time(6), invalidated boolean not null, start_of_conversation time(6), testing_day date, id bigint generated by default as identity, state_id bigint not null, strategy_id bigint, version bigint, secondary_id uuid, testing_scenario varchar(255) not null check (testing_scenario in ('OneBotTwoHumans','ThreeHumans')), primary key (id), unique (secondary_id));
create table messages (already_analysed_by_bot boolean, llm_generation_time integer not null, llm_token_count integer not null, conversation_id bigint not null, creation timestamp(6), id bigint generated by default as identity, photo_id bigint, player_id bigint not null, role_id bigint not null, version bigint, message TEXT not null, primary key (id));
create table participations (conversation_id bigint not null, player_id bigint not null, role_id bigint not null, username varchar(255) not null, primary key (conversation_id, player_id, role_id));
create table photos (creation timestamp(6), id bigint generated by default as identity, version bigint, description varchar(255) not null unique, name varchar(255) not null unique, resource_path varchar(255) not null unique, primary key (id));
create table players (is_bot boolean not null, creation timestamp(6), id bigint generated by default as identity, version bigint, secondary_id uuid, ip_address varchar(255) not null, system_role varchar(255) not null check (system_role in ('USER','ADMIN')), token oid, primary key (id), unique (secondary_id));
create table roles (id bigint generated by default as identity, name varchar(255) not null unique, primary key (id));
create table state_transitions (reason smallint check (reason between 0 and 2), conversation_id bigint not null, creation timestamp(6) not null, state_id bigint not null, version bigint, primary key (conversation_id, creation, state_id));
create table states (id bigint generated by default as identity, version bigint, name varchar(255) unique, primary key (id));
create table strategies (id bigint generated by default as identity, name varchar(255) unique, primary key (id));
create table strategies_by_role (role_id bigint not null, strategy_id bigint not null, evasion_example TEXT not null, example TEXT not null, script TEXT not null, primary key (role_id, strategy_id));
create table votes (round_no integer not null, conversation_id bigint not null, creation timestamp(6), player_id bigint not null, player_voted_against_id bigint, primary key (round_no, conversation_id, player_id));
create index idx_conversation_testing_scenario on conversations (testing_scenario);
create index idx_conversation_testing_day on conversations (testing_day);
create index idx_conversation_strategy_id on conversations (strategy_id);
create index idx_conversation_state_id on conversations (state_id);
create index idx_message_participation_id on messages (role_id, conversation_id, player_id);
create index idx_message_photo_id on messages (photo_id);
create index idx_message_creation on messages (creation);
create index idx_participation_username on participations (username);
create index idx_photo_name on photos (name);
create index idx_player_ip_address on players (ip_address);
create index idx_role_name on roles (name);
create index idx_state_transition_creation on state_transitions (creation);
create index idx_state_transition_reason on state_transitions (reason);
create index idx_state_name on states (name);
create index idx_strategy_name on strategies (name);
create index idx_vote_creation on votes (creation);
